import React, { Component } from "react";
import { Link } from "react-router-dom";
import book from "../book.json";
import Issue from "../Issue.json";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.css";
import Pagination from "./Pagination";
import { Paginate } from "./util/Paginate";

//import Toggle from "./Toggle";
import NavBar from "./NavBar";

class Book extends Component {
  state = {
    book: [],
    Issue: [],
    currentPage: 1,
    pageSize: 3,
    count: 0,
    bool: "true",
    s: "btn btn-success float-right ",
    //checked: false,
    // isEnable: true,
  };
  componentDidMount() {
    axios
      .get("http://localhost:7500/api/books")
      .then((response) => {
        //this.state.movie = response.data;
        this.setState({ book: response.data });
        console.log(book);
        //console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  /* issueBook = (id) => {
      const obj={
        name: this.state.name,
        email: this.state.email,
        password: this.state.password,
      }
    const user_id = localStorage.getItem("id");
    axios
      .post(`http://localhost:7500/book/${id}/issue/:${user_id}`)
      .then((res) => {
        //const issue = this.state.user.filter((c) => c._id !== id);
        this.setState({ Issue: issue });
      });
  };*/
  //updateUser = (id) => {};
  /*  disableUser = (id) => {
    axios.put(`http://localhost:9500/api/books/${id}`).then((res) => {
      //console.log(res.data);
      return this.props.history.push("/login");
      //this.setState({ count: this.state.count + 1 });
    });
  };
*/
  IssueBook = (book_id) => {
    const user_id = localStorage.getItem("id");

    //console.log(user_id);
    axios
      .post(`http://localhost:7500/api/book/${book_id}/issue/${user_id}`)
      .then((res) => {
        //const users = this.state.user.filter((c) => c._id !== book_id);
        console.log(res.data.book_info.stock);
        this.setState({ Issue: res.data });
        this.state.bool = false;
        alert("successfully issued by you");
      })
      .catch((error) => {
        console.log(error);
        //alert("")
      });
  };

  control = (id) => {
    this.setState({ count: this.state.count + 1 });
    // return this.state.count;
  };

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const { pageSize, currentPage, book: allbook } = this.state;
    const book = Paginate(allbook, currentPage, pageSize);
    return (
      <div className="App">
        <React.Fragment>
          <NavBar />
          <table className="table table-bordered table-hover table-lg  p-2 m-2">
            <thead>
              <tr>
                <th scope="col">Title</th>
                <th>ISBN</th>
                <th>Stock</th>
                <th>Author</th>
                <th scope="col">Issue</th>
              </tr>
            </thead>
            <tbody>
              {book.map((u, i) => {
                return (
                  <tr key={i}>
                    <td scope="row">{u.title}</td>
                    <td>{u.ISBN}</td>
                    <td>{u.stock}</td>
                    <td>{u.author}</td>

                    <td>
                      <button
                        type="button"
                        className={this.state.s}
                        onClick={() => {
                          this.IssueBook(u._id);
                        }}
                      >
                        Issue
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <Pagination
            itemsCount={this.state.book.length}
            pageSize={this.state.pageSize}
            currentPage={this.state.currentPage}
            onPageChange={this.handlePageChange}
          />
        </React.Fragment>
      </div>
    );
  }
}

export default Book;
