import React, { Component } from "react";
import book from "../book.json";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.css";
import Pagination from "./Pagination";
import { Paginate } from "./util/Paginate";

//import Toggle from "./Toggle";
import NavBar from "./NavBar";

class Book extends Component {
  state = {
    book: [],
    currentPage: 1,
    pageSize: 5,
    count: 0,
    //checked: false,
    // isEnable: true,
  };
  componentDidMount() {
    axios
      .get("http://localhost:7500/api/books")
      .then((response) => {
        //this.state.movie = response.data;
        this.setState({ book: response.data });
        console.log(book);
        //console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  issueBook = (id) => {
    const user_id = localStorage.getItem("id");
    axios.post(`http://localhost:7500/api/books/${id}`).then((res) => {
      const books = this.state.user.filter((c) => c._id !== id);
      this.setState({ book: books });
    });
  };
  //updateUser = (id) => {};
  /*  disableUser = (id) => {
    axios.put(`http://localhost:9500/api/books/${id}`).then((res) => {
      //console.log(res.data);
      return this.props.history.push("/login");
      //this.setState({ count: this.state.count + 1 });
    });
  };
*/
  control = (id) => {
    this.setState({ count: this.state.count + 1 });
    // return this.state.count;
  };

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const { pageSize, currentPage, book: allbook } = this.state;
    const book = Paginate(allbook, currentPage, pageSize);
    return (
      <div className="App">
        <React.Fragment>
          <NavBar />
          <table className="table table-bordered table-hover table-sm w-25 p-3 m-5">
            <thead>
              <tr>
                <th scope="col">Title</th>
                <th>ISBN</th>
                <th>Stock</th>
                <th>Author</th>
                <th scope="col">Delete</th>
              </tr>
            </thead>
            <tbody>
              {book.map((u, i) => {
                return (
                  <tr key={i}>
                    <td scope="row">{u.title}</td>
                    <td>{u.ISBN}</td>
                    <td>{u.stock}</td>
                    <td>{u.author}</td>

                    <td>
                      <button
                        type="button"
                        className="btn btn-danger float-right"
                        onClick={() => {
                          this.issueBook(u._id);
                        }}
                      >
                        Issue
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <Pagination
            itemsCount={this.state.book.length}
            pageSize={this.state.pageSize}
            currentPage={this.state.currentPage}
            onPageChange={this.handlePageChange}
          />
        </React.Fragment>
      </div>
    );
  }
}

export default Book;
