import React, { Component, Fragment } from "react";
import NavBar from "./NavBar";
import Pagination from "./Pagination";
import { Paginate } from "./util/Paginate";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios";
class Dashboard extends Component {
  state = {
    message: null,
  };

  componentDidMount() {
    axios
      .get("/api/messages")
      .then((response) => {
        //this.state.movie = response.data;

        this.setState({ message: [...response.data] });
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    const { pageSize, currentPage, message: allmessage } = this.state;
    const message = Paginate(allmessage, currentPage, pageSize);
    return (
      <Fragment>
        <NavBar />
        <table className="table table-bordered table-hover table-lg  p-2 m-2">
          <thead>
            <tr>
              <th scope="col">Title</th>
              <th>ISBN</th>
              <th>UserName</th>
              <th>Message</th>
            </tr>
          </thead>
          <tbody>
            {message.map((u, i) => {
              return (
                <tr key={i}>
                  <td scope="row">{u.book_info.title}</td>
                  <td>{u.book_info.ISBN}</td>

                  <td>{u.user_id.name}</td>
                  <td>{u.text}</td>

                  <td>
                    <button
                      type="button"
                      className="btn btn-primary float-right"
                      onClick={() => {
                        this.IssueBook(u._id);
                      }}
                    >
                      Allow
                    </button>
                  </td>

                  <td>
                    <button
                      type="button"
                      className={this.state.s}
                      onClick={() => {
                        this.ReturnBook(u._id);
                      }}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        )
        <Pagination
          itemsCount={this.state.book.length}
          pageSize={this.state.pageSize}
          currentPage={this.state.currentPage}
          onPageChange={this.handlePageChange}
        />
      </Fragment>
    );
  }
}

export default Dashboard;
