import React, { Component, Fragment } from "react";
import NavBar from "./NavBar";
class Dashboard extends Component {
  state = {
    message: null,
  };
  render() {
    return (
      <Fragment>
        <NavBar />
        <table className="table table-bordered table-hover table-lg  p-2 m-2">
          <thead>
            <tr>
              <th scope="col">Title</th>
              <th>ISBN</th>
              <th>UserName</th>
            </tr>
          </thead>
          <tbody>
            {this.state.message.map((u, i) => {
              return (
                <tr key={i}>
                  <td scope="row">{u.book_info.title}</td>
                  <td>{u.book_info.ISBN}</td>
                  <td>{u.user_id.name}</td>

                  <td>
                    <button
                      type="button"
                      className={
                        u.stock !== 0
                          ? "btn btn-primary float-right"
                          : "btn btn-primary float-right disabled"
                      }
                      onClick={() => {
                        this.RequestBook(u._id);
                      }}
                    >
                      Request
                    </button>
                  </td>

                  <td>
                    <button
                      type="button"
                      className={this.state.s}
                      onClick={() => {
                        this.ReturnBook(u._id);
                      }}
                    >
                      Return
                    </button>
                  </td>
                  <td>
                    <button
                      type="button"
                      className={this.state.s}
                      onClick={() => {
                        this.RenewBook(u._id);
                      }}
                    >
                      Renew
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        )}
        <Pagination
          itemsCount={this.state.book.length}
          pageSize={this.state.pageSize}
          currentPage={this.state.currentPage}
          onPageChange={this.handlePageChange}
        />
      </Fragment>
    );
  }
}

export default Dashboard;
