// ** create-user.component.js ** //

import React, { Component } from "react";
import axios from "axios";
import NavBar from "./NavBar";
import { Link, Redirect } from "react-router-dom";
import { json } from "body-parser";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      email: "",
      password: "",
      errors: {
        email: "",
        password: "",
      },
    };
  }

  onChangeEmail(e) {
    let value = e.target.value;
    let errors = this.state.errors;
    errors.email =
      this.state.email === " " || value.length < 3
        ? "email must contain 3 character"
        : "";
    let apos = value.indexOf("@");
    let dotpos = value.lastIndexOf(".");
    if (apos < 1 || dotpos - apos < 2) {
      errors.email = "please enter a valid email id";
    }
    this.setState({ email: e.target.value });
  }

  onChangePassword(e) {
    let value = e.target.value;
    let errors = this.state.errors;
    errors.password =
      value.length < 6 ? "password must contain atleast  6 character" : "";
    this.setState({ password: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();
    let errors = this.state.errors;
    if (this.state.email === "") {
      errors.email = "Can not be null";
    } else if (this.state.password === "") {
      errors.password = "Can not be null";
    } else if (this.state.errors.email.length !== 0) {
      errors.email = this.state.errors.email;
    } else if (this.state.errors.password.length !== 0) {
      errors.password = this.state.errors.password;
    }

    const userObject = {
      email: this.state.email,
      password: this.state.password,
    };

    axios
      .post("/api/auth", userObject)
      .then((res) => {
        //console.log(res.data.id);
        localStorage.setItem("login", JSON.stringify(res));
        localStorage.setItem("role", res.data.role);
        localStorage.setItem("id", res.data.id);
        localStorage.setItem("name", res.data.name);

        //const a = localStorage.getItem("role");
        console.log(res.data.id);
        return this.props.history.push("/");
      })
      .catch((error) => {
        console.log(error.msg);
        if (
          this.state.errors.email.length === 0 &&
          this.state.errors.password.length === 0
        ) {
          toast.error("user not exists or username and password incorrect");
        }
        // alert(error.errors);
      });

    this.setState({ email: "", password: "" });
  }

  render() {
    const { errors } = this.state;
    return (
      <React.Fragment>
        <ToastContainer />
        <NavBar />
        <div className="wrapper m-5 ">
          <form onSubmit={this.onSubmit}>
            <div className="form-group m-3">
              <label forhtml="email">Email</label>
              <input
                autoFocus
                autoComplete
                type="text"
                id="email"
                placeholder="Email"
                value={this.state.email}
                onChange={this.onChangeEmail}
                className="form-control"
              />
              <p>
                <span style={{ color: "Pink" }}>{errors.email}</span>
              </p>
            </div>
            <div className="form-group m-3 ">
              <label forhtml="password">Password</label>
              <input
                autoFocus
                autoComplete
                type="password"
                id="password"
                placeholder="Password"
                value={this.state.password}
                onChange={this.onChangePassword}
                className="form-control"
              />
              <p>
                <span style={{ color: "Pink" }}>{errors.password}</span>
              </p>
            </div>
            <div className="form-group m-3">
              <input type="submit" value="Login" className="btn btn-success" />
              <div
                class="modal fade"
                id="exampleModalCenter"
                tabindex="-1"
                role="dialog"
                aria-labelledby="exampleModalCenterTitle"
                aria-hidden="true"
              >
                <div class="modal-dialog modal-dialog-centered" role="document">
                  <div class="modal-content">
                    <div class="modal-header">
                      <h5 class="modal-title" id="exampleModalLongTitle">
                        Modal title
                      </h5>
                      <button
                        type="button"
                        class="close"
                        data-dismiss="modal"
                        aria-label="Close"
                      >
                        <span aria-hidden="true">&times;</span>
                      </button>
                    </div>
                    <div class="modal-body">...</div>
                    <div class="modal-footer">
                      <button
                        type="button"
                        class="btn btn-secondary"
                        data-dismiss="modal"
                      >
                        Close
                      </button>
                      <button type="button" class="btn btn-primary">
                        Save changes
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div>
              <Link to="/forget-password">Forget Password</Link>
              <hr /> New_User ?<Link to="/register">Register</Link>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}
