export default class Login extends Component {
  constructor(props) {
    super(props);

    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      email: "",
      password: "",
      errors: {
        email: "",
        password: "",
      },
    };
  }

  onChangeEmail(e) {
    let value = e.target.value;
    let errors = this.state.errors;
    errors.email = value.length < 3 ? "email must contain 3 character" : "";
    let apos = value.indexOf("@");
    let dotpos = value.lastIndexOf(".");
    if (apos < 1 || dotpos - apos < 2) {
      errors.email = "please enter a valid email id";
    }
    this.setState({ email: e.target.value });
  }

  onChangePassword(e) {
    let value = e.target.value;
    let errors = this.state.errors;
    errors.password =
      value.length < 6 ? "password must contain atleast  6 character" : "";
    this.setState({ password: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    const userObject = {
      email: this.state.email,
      password: this.state.password,
    };
    if (email === "") {
      email.errors = "can not be blank";
    }

    axios
      .post("/api/auth", userObject)
      .then((res) => {
        //console.log(res.data.id);
        localStorage.setItem("login", JSON.stringify(res));
        localStorage.setItem("role", res.data.role);
        localStorage.setItem("id", res.data.id);
        localStorage.setItem("name", res.data.name);

        //const a = localStorage.getItem("role");
        console.log(res.data.id);
        return this.props.history.push("/");
      })
      .catch((error) => {
        console.log(error.msg);
        toast.error("user not exists or username and password incorrect");
        // alert(error.errors);
      });

    this.setState({ email: "", password: "" });
  }

  render() {
    const { errors } = this.state;
    return (
      <React.Fragment>
        <ToastContainer />
        <NavBar />
        <div className="wrapper m-5 ">
          <form onSubmit={this.onSubmit}>
            <div className="form-group m-3">
              <label forhtml="email">Email</label>
              <input
                autoFocus
                autoComplete
                required
                type="text"
                id="email"
                placeholder="Email"
                value={this.state.email}
                onChange={this.onChangeEmail}
                className="form-control"
              />
              <p>
                <span style={{ color: "Pink" }}>{errors.email}</span>
              </p>
            </div>
            <div className="form-group m-3 ">
              <label forhtml="password">Password</label>
              <input
                autoFocus
                autoComplete
                required
                type="password"
                id="password"
                placeholder="Password"
                value={this.state.password}
                onChange={this.onChangePassword}
                className="form-control"
              />
              <p>
                <span style={{ color: "Pink" }}>{errors.password}</span>
              </p>
            </div>
            <div className="form-group m-3">
              <input type="submit" value="Login" className="btn btn-success" />
            </div>
            <div>
              <Link to="/forget-password">Forget Password</Link>
              <hr /> New_User ?<Link to="/register">Register</Link>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}
