import React, { Component } from "react";
import user1 from "../user1.json";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.css";
import Pagination from "./Pagination";
import { Paginate } from "./util/Paginate";
import { Link } from "react-router-dom";
//import Toggle from "./Toggle";
import NavBar from "./NavBar";
import Register from "./Register";

class Mybook extends Component {
  state = {
    user1: [],
    currentPage: 1,
    pageSize: 4,
    count: 0,
    //checked: false,
    // isEnable: true,
  };

  componentDidMount() {
    const id = localStorage.getItem("id");
    console.log(id);
    axios
      .get(`/api/users/${id}`)
      .then((response) => {
        //this.state.movie = response.data;
        const user1 = [];
        this.setState({ user1: response.data });
        //response.data;

        console.log(user1);
        //console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  control = (id) => {
    this.setState({ count: this.state.count + 1 });
    // return this.state.count;
  };

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    //this.getUserBook(id);
    const { pageSize, currentPage, user1: alluser } = this.state;
    const user1 = Paginate(alluser, currentPage, pageSize);
    return (
      <div className="App">
        <React.Fragment>
          <NavBar />
          <table className="table table-bordered table-hover table-lg w-30 p-2 m-3">
            <thead>
              <tr>
                <th scope="col">Book_id</th>
                <th>Book_Name</th>
              </tr>
            </thead>
            <tbody>
              {user1.map((u, i) => {
                return (
                  <tr key={i}>
                    <td scope="row">{u._id}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <Pagination
            itemsCount={this.state.user1.length}
            pageSize={this.state.pageSize}
            currentPage={this.state.currentPage}
            onPageChange={this.handlePageChange}
          />
        </React.Fragment>
      </div>
    );
  }
}

export default Mybook;
